import React from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, Select, InputNumber, Upload, Icon, message } from 'antd';
import { connect } from "react-redux";
import { addPoint } from '../actions/addpoint';
import { setPoints } from '../actions/setpoints';
import { updatePoint } from '../actions/updatepoint';
import FormItem from 'antd/lib/form/FormItem';
const { Option } = Select;
const { TextArea } = Input;

class AddProblemForm extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
    };
    handleSubmit = e => {
      e.preventDefault();
      this.props.form.validateFieldsAndScroll((err, values) => {
        if (!err) {
          const newProblem = {
            category: values.category,
            longitude: values.longitude,
            latitude: values.latitude,
            description: values.description
        };
          const newPoint = {
            latitude: values.latitude,
            longitude: values.longitude
          }
        console.log(newProblem);
        fetch("http://localhost:3001/add_problem", {
            method: 'POST',
            body: JSON.stringify(newProblem),
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                },
        }).then(response => response.json())
        .then(response => {console.log('response: ', response)})
        .then(() => {
        fetch("http://localhost:3001/add_point", {
          method: 'POST',
          body: JSON.stringify(newPoint),
          headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
              },
        }).then(res => {
          console.log("resp: ", res);
          if(res.status === 200) {
            this.props.addPoint(newPoint);
          }
          else if(res.status === 201) {
            this.props.updatePoint(newPoint);
          }
          res.json()})
        .then(res => {console.log('response: ', res)})
        .then(() => {
          console.log("jdgfhdghfd");
        })
      })
        .catch(error => console.log('error:', error));
      }
      this.props.form.resetFields();
      })};

    handleChange = (value) => {
        console.log(`selected ${value}`);
    };

    handleConfirmBlur = e => {
      const { value } = e.target;
      this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };

    render() {

      const props = {
        name: 'file',
        action: 'http://localhost:3001/add_image',
        headers: {
          authorization: 'authorization-text',
        },
        onChange(info) {
          if (info.file.status !== 'uploading') {
            console.log(info.file, info.fileList);
          }
          if (info.file.status === 'done') {
            message.success(`${info.file.name} file uploaded successfully`);
          } else if (info.file.status === 'error') {
            message.error(`${info.file.name} file upload failed.`);
          }
        },
      };
      const { getFieldDecorator } = this.props.form;
      const formStyle = {
          marginLeft: '20%',
          marginTop: '3%',
          width: '50%'
      }

      const formItemLayout = {
        labelCol: {
          xs: { span: 24 },
          sm: { span: 8 },
        },
        wrapperCol: {
          xs: { span: 24 },
          sm: { span: 16 },
        },
      };
      const tailFormItemLayout = {
        wrapperCol: {
          xs: {
            span: 24,
            offset: 0,
          },
          sm: {
            span: 16,
            offset: 8,
          },
        },
      };
       return (
        <Form {...formItemLayout} onSubmit={this.handleSubmit} className='form' style={formStyle}>
             <Form.Item label={<span>Category</span>}>
             {getFieldDecorator('category', {
              rules: [
                {
                  required: true,
                  message: 'Select the category!',
                },
              ],
            })(<Select style={{ width: '100%' }} onChange={this.handleChange}>
                <Option value="0">Garbage disposal is not done on time</Option>
                <Option value="1">Trash has no lid</Option>
                <Option value="2">Trash is placed in the wrong place</Option>
            </Select>)}
          </Form.Item>
          <Form.Item label="Latitude">
            {getFieldDecorator('latitude', {
              rules: [
                {
                  validator(rule, value, callback) {
                    if(value > 90) {
                      callback('gfgfgf');
                      return;
                    }
                    else {
                      callback();
                    }
                  } 
                },
                {
                  required: true,
                  message: 'Latitude is required!',
                },
                {

                }
              ],
            })(<InputNumber style={{width: '100%'}} />)}
          </Form.Item>
          <Form.Item label="Longitude">
            {getFieldDecorator('longitude', {
              rules: [
                {
                  type: 'number',
                  message: 'The input is not valid',
                },
                {
                  required: true,
                  message: 'Longitude is required!',
                },
              ],
            })(<InputNumber style={{width: '100%'}}/>)}
          </Form.Item>   
          <Form.Item label="Description">
            {getFieldDecorator('description', {
              rules: [
                {
                  type: 'string',
                },
                {
                  required: false,
                  message: 'Please input the longitude!',
                },
              ],
            })(<TextArea rows={4} />)}
          </Form.Item>  
          <FormItem>
          <Upload {...props}>
            <Button>
            <Icon type="upload" /> Click to Upload
            </Button>
          </Upload>,
          </FormItem>   
         <Form.Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Add
            </Button>
          </Form.Item>
        </Form>
      );
    }
  }

const WrappedAddProblemForm = Form.create({ name: 'register' })(AddProblemForm);
export default connect(
  state => ({
    points: state.points,
    }),
  dispatch => ({
    addPoint: (data => {
      dispatch(addPoint(data))
    }),
    setPoints: ((data) => {
      dispatch(setPoints(data))
    }),
    updatePoint: ((data) => {
      dispatch(updatePoint(data))
    }),
}))(WrappedAddProblemForm);
