import React from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, Select, InputNumber } from 'antd';
const { Option } = Select;
const { TextArea } = Input;

class AddProblemForm extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
    };

    handleSubmit = e => {
        e.preventDefault();
      this.props.form.validateFieldsAndScroll((err, values) => {
        if (!err) {
          const newProblem = {
            category: values.category,
            longitude: values.longitude,
            latitude: values.latitude,
            description: values.description
        };
        console.log(newProblem);

        fetch("/api/add", {
            method: 'POST',
            body: JSON.stringify(newProblem),
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                },
        }).then(console.log("gfgfgfgfg"))
        .then(response => response.json())
        .then(response => {console.log('response: ', response);
      })
        .catch(error => console.log('error:', error));
        }
      });
      this.props.form.resetFields();
    };

    handleChange = (value) => {
        console.log(`selected ${value}`);
    };

    handleConfirmBlur = e => {
      const { value } = e.target;
      this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };

    render() {
      const { getFieldDecorator } = this.props.form;
      const formStyle = {
          marginLeft: '20%',
          marginTop: '3%',
          width: '50%'
      }

      const formItemLayout = {
        labelCol: {
          xs: { span: 24 },
          sm: { span: 8 },
        },
        wrapperCol: {
          xs: { span: 24 },
          sm: { span: 16 },
        },
      };
      const tailFormItemLayout = {
        wrapperCol: {
          xs: {
            span: 24,
            offset: 0,
          },
          sm: {
            span: 16,
            offset: 8,
          },
        },
      };
       return (
        <Form {...formItemLayout} onSubmit={this.handleSubmit} className='form' style={formStyle}>
             <Form.Item label={<span>Category</span>}>
             {getFieldDecorator('category', {
              rules: [
                {
                  required: true,
                },
              ],
            })(<Select style={{ width: 120 }} onChange={this.handleChange}>
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
                <Option value="Yiminghe">yiminghe</Option>
            </Select>)}
          </Form.Item>
          <Form.Item label="Latitude">
            {getFieldDecorator('latitude', {
              rules: [
                {
                  type: 'number',
                  message: 'The input is not valid',
                },
                {
                  required: true,
                  message: 'Latitude is required!',
                },
              ],
            })(<InputNumber style={{width: '100%'}} />)}
          </Form.Item>
          <Form.Item label="Longitude">
            {getFieldDecorator('longitude', {
              rules: [
                {
                  type: 'number',
                  message: 'The input is not valid',
                },
                {
                  required: true,
                  message: 'Longitude is required!',
                },
              ],
            })(<InputNumber style={{width: '100%'}}/>)}
          </Form.Item>   
          <Form.Item label="Description">
            {getFieldDecorator('description', {
              rules: [
                {
                  type: 'string',
                },
                {
                  required: false,
                  message: 'Please input the longitude!',
                },
              ],
            })(<TextArea rows={4} />)}
          </Form.Item>         
         <Form.Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Add
            </Button>
          </Form.Item>
        </Form>
      );
    }
  }

const WrappedAddProblemForm = Form.create({ name: 'register' })(AddProblemForm);
export default WrappedAddProblemForm;
