class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.handleFetch();
  }
  handleFetch = () => {
    fetch("http://localhost:3001/points",  {
      method: 'GET',
      headers: {
         'Content-Type': 'application/x-www-form-urlencoded',
      },
  }).then(response => response.json())
  .then(response => {
    this.props.setPoints({points: response.points});
    console.log(response);
  })
  .catch(error => console.log('error:', error)); 
}
componentDidUpdate() {
  console.log("componentWillUpdate");
  console.log("this.props.points: ", this.props.points);
}

displayMarkers = () => {
   return this.props.points.map((point, index) => {
    console.log("mtavvvv");
    return (<Circle
    key={index}
    center={{latitude: 4,longitude: 4}}
    radius={1000}
    options={{
        strokeColor: "#66009a",
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: `#66009a`,
        fillOpacity: 0.35,
        zIndex: 1
    }}
/>)
  })
}
  render() {
    const mapStyles = {
      width: '100%',
      height: '100vh',
    };

    return (
      <GoogleMapReact
      google={this.props.google}
      zoom={8}
      style={mapStyles}
      initialCenter={{ lat: 4, lng: 5}}
      >
      {this.displayMarkers()}
      </GoogleMapReact>
    )
  }
}

export default connect(
  state => ({
    points: state.points,
    }),
  dispatch => ({
    addPoint: (data => {
      dispatch(addPoint(data))
    }),
    setPoints: ((data) => {
      dispatch(setPoints(data))
    }),
}))(GoogleApiWrapper({
  apiKey: 'AIzaSyCR1rxt8xnpURsVA3bDm8eukPy4EmW9icQ'
})(MapContainer));
