import React from 'react';
import { Map, GoogleApiWrapper } from 'google-maps-react';
import CustomMarker from './marker';
import { connect } from "react-redux";
import { addPoint } from '../actions/addpoint';
import { setPoints } from '../actions/setpoints';
import '../App.css';
const AnyReactComponent = ({ text }) => <div style={{
  color: 'white', 
  background: 'grey',
  padding: '15px 10px',
  display: 'inline-flex',
  textAlign: 'center',
  alignItems: 'center',
  justifyContent: 'center',
  borderRadius: '100%',
  transform: 'translate(-50%, -50%)'
}}>
  {text}
</div>;

class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.handleFetch();
  }
  handleFetch = () => {
    fetch("http://localhost:3001/points",  {
      method: 'GET',
      headers: {
         'Content-Type': 'application/x-www-form-urlencoded',
      },
  }).then(response => response.json())
  .then(response => {
    this.props.setPoints({points: response.points});
    console.log(response);
  })
  .catch(error => console.log('error:', error)); 
}
componentDidUpdate() {
  console.log("componentWillUpdate");
  console.log("this.props.points: ", this.props.points);
}

displayMarkers = () => {
  return this.props.points.map((point, index) => {
    return <CustomMarker key={index} id={index} position={{
     lat: point.latitude,
     lng: point.longitude
   }}
   onClick={() => console.log("You clicked me!")} />
  })
}
  render() {
    const mapStyles = {
      width: '100%',
      height: '100vh',
    };

    return (
      <Map
      google={this.props.google}
      zoom={8}
      style={mapStyles}
      initialCenter={{ lat: 4, lng: 5}}
      >
      {this.displayMarkers()}
      </Map>
    )
  }
}

export default connect(
  state => ({
    points: state.points,
    }),
  dispatch => ({
    addPoint: (data => {
      dispatch(addPoint(data))
    }),
    setPoints: ((data) => {
      dispatch(setPoints(data))
    }),
}))(GoogleApiWrapper({
  apiKey: 'AIzaSyCR1rxt8xnpURsVA3bDm8eukPy4EmW9icQ'
})(MapContainer));
